const FacultyList = () => {
  const [selectedFaculty, setSelectedFaculty] = useState(null);

  const handleCardClick = (faculty) => {
    setSelectedFaculty(faculty);
  };

  const closeModal = () => {
    setSelectedFaculty(null);
  };

  return (
    <div className={styles.container}>
      <h1 className={styles.heading}>Our Faculty</h1>
      <div className={styles.grid}>
        {facultyData.map((faculty, index) => (
          <FacultyCard
            key={index}
            icon={faculty.icon}
            title={faculty.title}
            onClick={() => handleCardClick(faculty)}
          />
        ))}
      </div>
      {selectedFaculty && (
        <Modal onClose={closeModal}>
          <div className={styles.modalNavbar}>
            <div>{selectedFaculty.icon}</div>
            <div onClick={closeModal}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="13"
                height="13"
                viewBox="0 0 13 13"
                fill="none"
              >
                <path
                  d="M1.25 1.375L6.5 6.625L11.75 1.375"
                  stroke="#BE121F"
                  strokeWidth="1.5"
                />
                <path
                  d="M1.25 12.125L6.5 6.875L11.75 12.125"
                  stroke="#BE121F"
                  strokeWidth="1.5"
                />
              </svg>
            </div>
          </div>
          <ol>
            {selectedFaculty.members.map((member, idx) => (
              <li className={styles.list} key={idx}>
                {member}
              </li>
            ))}
          </ol>
        </Modal>
      )}
    </div>
  );
};

export default FacultyList;
